import { Component, ViewEncapsulation } from '@angular/core';
import { cloneDeep } from 'lodash-es';
import { SimpleEntity, SmzDragEventData } from 'ngx-smz-ui';
import { CountryService } from './country.service';
declare const window;

@Component({
    selector: 'app-home',
    templateUrl: `home.component.html`,
    styleUrls: ['home.component.scss'],
    encapsulation: ViewEncapsulation.None,
    host: { 'class': 'absolute inset-0 p-3' },
    providers: [CountryService],
    standalone: false
})
export class HomeComponent
{
  public pdfUrl = 'http://www.africau.edu/images/default/sample.pdf';
  public imagePath;
  public pdfBase64 = 'data:application/pdf;base64,';
  public groupA = [{ id: 1, name: 'bg-blue-500'}, { id: 2, name: 'bg-blue-500'}, { id: 3, name: 'bg-blue-500'}, { id: 4, name: 'bg-blue-500'}];
  public groupB = [{ id: 5, name: 'bg-green-500'}, { id: 6, name: 'bg-green-500'}, { id: 7, name: 'bg-green-500'}, { id: 8, name: 'bg-green-500'}];
  public current: SimpleEntity<number>;
  public isMinimized = false;
  constructor()
  {
    this.imagePath = 'assets/logo.png';

    setTimeout(() => {
      this.imagePath = 'assets/logo2.png';

      setTimeout(() => {
        this.imagePath = 'assets/logo.png';
      }, 3000);
    }, 3000);

  }

  public speak(): void {
    const voices = window.speechSynthesis?.getVoices();

    const voice = voices?.find((voice) => /pt-BR/.test(voice.lang));
    const utterance = new SpeechSynthesisUtterance(voice);
    console.log(1);


    utterance.text = 'Existem várias bibliotecas conhecidas de text to speech (TTS) que podem ser usadas em aplicativos Angular. Algumas das bibliotecas mais conhecidas são:'; // word é a palavra que desejamos pronunciar
    utterance.lang = 'pt-BR'; // língua a pronunciar
    //utterance.voice = voice; // voz defina acima
    utterance.rate = 1; // velocidade de fala
    console.log(2);

    // setTimeout(() => { window.speechSynthesis.speak(utterance); console.log(3); }, 2000)

    window.speechSynthesis.speak(utterance);

    console.log(22);
  }

  public dragStart(event: SmzDragEventData): void {
    this.current = event.data;
  }

  public dragEnd(): void {
    this.current = null;
  }

  public dropToA(): void {
    const current = cloneDeep(this.current);
    this.current = null;

    this.groupA = [...this.groupA, current];
    this.groupB = this.groupB.filter(x => x.id != current.id)
  }

  public dropToB(): void {
    const current = cloneDeep(this.current);
    this.current = null;

    this.groupA = this.groupA.filter(x => x.id != current.id)
    this.groupB = [...this.groupB, current];
  }

  public minimize(): void {
    this.isMinimized = true;
  }

  public restore(): void {
    this.isMinimized = false;
  }

}