import { Component, ViewEncapsulation } from '@angular/core';
import { Store } from '@ngxs/store';
import { SmzDialogsService } from 'ngx-smz-ui';
import { FilterService, SelectItemGroup } from 'primeng/api';
import { CountryService } from './country.service';

@Component({
  selector: 'app-home',
  templateUrl: `home.component.html`,
  styleUrls: ['home.component.scss'],
  encapsulation: ViewEncapsulation.None,
  host: { 'class': 'absolute inset-0 p-3' },
  providers: [CountryService]
})
export class HomeComponent
{
  public pdfUrl = 'http://www.africau.edu/images/default/sample.pdf';
  public imagePath;
  public pdfBase64 = 'data:application/pdf;base64,';
  selectedCountry: any;
  filteredCountries: any[];

  selectedCountries: any[];
  countries: any[];
  groupedCities: SelectItemGroup[];
  filteredGroups: any[];
  constructor(private dialogs: SmzDialogsService, private store: Store,
    private countryService: CountryService,
    private filterService: FilterService)
  {
    this.imagePath = 'assets/logo.png';

    setTimeout(() => {
      this.imagePath = 'assets/logo2.png';

      setTimeout(() => {
        this.imagePath = 'assets/logo.png';
      }, 3000);
    }, 3000);

    this.countryService.getCountries().then(countries => {
      this.countries = countries;
    });

    this.groupedCities = [
      {
        label: "Germany",
        value: "de",
        items: [
          { label: "Berlin", value: "Berlin" },
          { label: "Frankfurt", value: "Frankfurt" },
          { label: "Hamburg", value: "Hamburg" },
          { label: "Munich", value: "Munich" }
        ]
      },
      {
        label: "USA",
        value: "us",
        items: [
          { label: "Chicago", value: "Chicago" },
          { label: "Los Angeles", value: "Los Angeles" },
          { label: "New York", value: "New York" },
          { label: "San Francisco", value: "San Francisco" }
        ]
      },
      {
        label: "Japan",
        value: "jp",
        items: [
          { label: "Kyoto", value: "Kyoto" },
          { label: "Osaka", value: "Osaka" },
          { label: "Tokyo", value: "Tokyo" },
          { label: "Yokohama", value: "Yokohama" }
        ]
      }
    ];
  }

  filterCountry(event) {
    //in a real application, make a request to a remote url with the query and return filtered results, for demo we filter at client side
    let filtered: any[] = [];
    let query = event.query;
    for (let i = 0; i < this.countries.length; i++) {
      let country = this.countries[i];
      if (country.name.toLowerCase().indexOf(query.toLowerCase()) == 0) {
        filtered.push(country);
      }
    }

    this.filteredCountries = filtered;
  }

  filterGroupedCity(event) {
    let query = event.query;
    let filteredGroups = [];

    for (let optgroup of this.groupedCities) {
      let filteredSubOptions = this.filterService.filter(
        optgroup.items,
        ["label"],
        query,
        "contains"
      );
      if (filteredSubOptions && filteredSubOptions.length) {
        filteredGroups.push({
          label: optgroup.label,
          value: optgroup.value,
          items: filteredSubOptions
        });
      }
    }

    this.filteredGroups = filteredGroups;
  }

}